css:
.product-card__buy.fade {
  opacity: 1 !important;
}
.product-card__buy .btn-primary {
    background-color: #25BA34 !important;
    border-color: #25BA34 !important;
}
.minicart__btn-checkout {
    background-color: #25BA34 !important;
    border-color: #25BA34 !important;
}
@media(max-width: 767px) {
  .top-bar__countdown {
    font-size: 17px!important;
  }
}
@media(min-width: 767px) {
  .top-bar__countdown {
    font-size: 15px!important;
  }
}
.progress-bar {
    background-color: #25BA34!important;
}
.progress-bar span, .progress-bar strong {
    font-size: 16px; 
}
.breadcrumb-item .i-home {
  color: var(--primary) !important;
}

.breadcrumb-item.active {
  color: var(--secondary) !important;
}
