css:
.product-card__buy.fade {
  opacity: 1 !important;
}
.product-card__buy button {
background-color: #25BA34;
border-color: #25BA34;
}
@media(max-width: 767px) {
  .top-bar__countdown {
    font-size: 17px!important;
  }
}
@media(min-width: 767px) {
  .top-bar__countdown {
    font-size: 15px!important;
  }
}
.progress-bar {
    background-color: #25BA34!important;
}
.progress-bar span, .progress-bar strong {
    font-size: 1.2em; 
}
